	@Override
	public String infobuscaDetalhada(String valor, String uf, Long codigoTipoConsulta,  Long codigoUsuarioEmpresa) throws Exception {
		try {				
			Parametro UsuarioSenhaAplicacao = this.buscarParametro(USUARIO_SENHA_APLICACAO);
			String authorization = Util.gerarBase64(UsuarioSenhaAplicacao.getUsuario() + ":" + UsuarioSenhaAplicacao.getSenha());
			Parametro parametroCPF = this.buscarParametro(INFOBUSCA_DETALHADA);

			MultiValueMap<String, String> headers = new LinkedMultiValueMap<String, String>();
			Map map = new HashMap<String, String>();
			map.put("Authorization", "Basic " + authorization);
			map.put("api-token", parametroCPF.getValor1());
			headers.setAll(map);
			Map req_payload = new HashMap();
			req_payload.put("produto", parametroCPF.getValor2());
			
			if (codigoTipoConsulta.equals(INFOBUSCA_DETALHADA_PF)) {
				req_payload.put("param", "cpf");				
			}
			if (codigoTipoConsulta.equals(INFOBUSCA_DETALHADA_PJ)) {
				req_payload.put("param", "cnpj");
			}
			
			req_payload.put("value", valor);
			req_payload.put("uf", uf);

			HttpEntity<?> request = new HttpEntity<>(req_payload, headers);
			String url = parametroCPF.getValor3();

			// Onde chama o serviço de fato
			//ResponseEntity<?> response = new RestTemplate().postForEntity(url, request, String.class);
			//String json = response.getBody().toString();
			
			String json = "";
			String jsonSalvo = this.buscarConsultaSalvaPorData(valor, uf, codigoTipoConsulta, codigoUsuarioEmpresa);
			
			// Se ja existir uma consulta do mesmo dia, pega ela e nao vai na sefaz novamente
			if (jsonSalvo != null) {
				json = jsonSalvo;
			} else {
				if (codigoTipoConsulta.equals(INFOBUSCA_DETALHADA_PF)) {
					json =  buscaJson("INFOBUSCA_DETALHADA_PF");								
				}
				if (codigoTipoConsulta.equals(INFOBUSCA_DETALHADA_PJ)) {
					json =  buscaJson("INFOBUSCA_DETALHADA_PJ");
				}				
			}			

			// Salva o retorno
			if (json != null && json.length() > 0) {
				RetornoConsulta retornoSalvo = this.salvarRetorno(codigoTipoConsulta, codigoUsuarioEmpresa, json, valor, uf);				
			}
		     return json;
		} catch (Exception e) {
			throw new Exception("Falha ao consultar o CPF: " + e.getMessage());
		}
	}
	
